sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	 // 1. Create an object to store rows from a table
        var grNeedIt = new GlideRecord('x_58872_needit_needit');

        // 2. Build query
        grNeedIt.addQuery('state','!=',"3");
		//grNeedIt.addQuery('active','=',true);
	    grNeedIt.addQuery('number','!=',current.number);
        grNeedIt.addQuery('u_requested_for','=',current.u_requested_for);

        // 3. Execute query 
        grNeedIt.query();

        // Initialize message to list Caller's active Incidents
        var msg = "User's other active NeedIt Requests: ";

        // 4. Process returned records
        while(grNeedIt.next()){
            // add a new line to the message for each of the User's active Requests 
            msg = msg + grNeedIt.getValue('u_what_needed') + ": " + grNeedIt.getValue('short_description') + " | ";
        }

        gs.addInfoMessage(msg);

})(current, previous);
